import React, { Component } from 'react'

class GoBackButton extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            displayButton: false
        }
    }
    

    componentDidMount() {
        console.log('hash', window.location.href);

    }

    goToPrevPage = () => {
        if (window.location.href === 'http://localhost:3000/main') {
            console.log('testX' ,window.location.href);
        }
        else {
            console.log('testX' , window.location.href);
            return window.history.back();
        }
    }

    render() {
        return (
            <div>
                <button onClick={this.goToPrevPage}>Go back top previous page</button>
            </div>
        )
    }
}

export default GoBackButton
