import React, { Component } from 'react'

class GoBackButton extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            location: null,
            displayButton: null
        }
    }

    componentDidMount() {
        let location = window.location.href
        if (window.location.href === 'http://localhost:3000/main') {
            this.setState({ displayButton: 'none', location: location });
        }
        else {
            this.setState({ displayButton: 'block' })
        }
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevState.location == this.state.location) {
        console.log('hash', window.location.href);
        if (window.location.href === 'http://localhost:3000/main') {
            this.setState({ displayButton: 'none' })
        }
        else {
            this.setState({ displayButton: 'block' })
        }
    }
    }

    goToPrevPage = () => {
        return window.history.back();
    }

    render() {
        const { displayButton } = this.state;

        return (
            <div>
                <button style={{ display: displayButton }} onClick={this.goToPrevPage}>Go back top previous page</button>
            </div>
        )
    }
}

export default GoBackButton
