import React, { Component } from 'react'

import { Dropbox } from "dropbox";
import { Link } from 'react-router-dom'

import LogOut from './LogOut';

import '../Css/nav.css'
import folderImg from '../Img/folder-img.png';
import fileImg from '../Img/file-img.png';

class Main extends Component {
    constructor(props) {
        super(props)

        this.state = {
          folders: [],
          thumbnail: {}
        }
    }

    componentDidMount() {
      const dbx = new Dropbox({ accessToken: localStorage.getItem("token") });
      dbx.filesListFolder({ path: "" })
        .then((res) => {
          console.log(res.entries);
          this.setState({ folders: res.entries });
        });


        // get images from image files!
        // dbx.filesGetThumbnailBatch({
        //   entries: [{
        //     path: '/image1.png',
        //     size: 'w32h32',
        //     format: 'png'
        //   }]
        // })
        // .then(res => {
        //   console.log(res);
        // })

        dbx.filesGetThumbnail({"path": "/image1.png"})
        .then(res => {
          // console.log(res);
          this.setState({ thumbnail: res });
        })




        // filter!
        // let filtered = this.state.folders.filter(file => file['.tag'] === 'file');
        // console.log(filtered);
        const { folders } = this.state;
        folders.map(x => console.log(x));
    }

    componentDidUpdate() {
      const { folders } = this.state;
      folders.map(files => {
        const type = files['.tag'];

        if (type === 'file') {
          this.setState({  })
        }
      });
    }

    render() {
      const { folders, thumbnail } = this.state;

        return (
          <div className="App">
        <div className="sideLeft">
          <div>
            Logo
          </div>
          <ul>
            <li> Link 1 </li>
            <li> Link 7 </li>
            <li> Link 3 </li>
            <li> Link 1 </li>
          </ul>
        </div>

        <div className={"bigBox"}>
          <header>
            <input placeholder="Search" type="text" />
            <LogOut />
          </header>

          <main>
          <div className="files">
                <table>
                    <thead>
                      <tr>
                        <th>Folder/file name</th>
                      </tr>
                  </thead>

                  <tbody>
                    {folders.map(folder => {
                      // render img icon to files and folders!
                      const type = folder['.tag'];
                      let folderThumbnail
                      type === 'folder' ? folderThumbnail = folderImg : folderThumbnail = fileImg;

                      // let objectUrl = URL.createObjectURL(thumbnail.fileBlob);
                      return (
                        <tr>
                          <div>
                            <Link to={`/folder${folder.path_display}`} style={{ border: '1px solid' }}>
                              <td style={{ display: 'flex' }}>
                                <img src={folderThumbnail} style={{ height: '42px', width: '42px' }} alt=""/>
                                <p>{folder.name}</p>
                              </td>
                            </Link>

                          </div>
                        </tr>
                      )
                    })}
                </tbody>
                </table>
            </div>

            <div className="sidebarRight">
            <ul>
                <li> Link 1 </li>
                <li> Link 1 </li>
                <li> Link 1 </li>
                <li> Link 1 </li>
            </ul>
            </div>
          </main>
        </div>
    </div>
      )
    }
}

export default Main
